package gpumaths

import (
	"gitlab.com/elixxir/crypto/cyclic"
	"gitlab.com/elixxir/crypto/large"
)

func makeTestGroup4096() *cyclic.Group {
	p := large.NewIntFromString("", 16)
	return cyclic.NewGroup(
		p,
		large.NewInt(2),
	)
}

func makeTestGroup2048() *cyclic.Group {
	p := large.NewIntFromString("", 16)
	return cyclic.NewGroup(
		p,
		large.NewInt(2),
	)
}
